mapper.select:
SELECT

from people_retire
where code = #{code, jdbcType=BIGINT}

SELECT t.id, t.code, t.name, t.retire_job_name, t.retire_job_level_id, j.job_level as retire_job_level_name, t.sex, t.national_id, s.name as national_name, t.education_name, t.birthday, t.political_name, t.work_date, t.retire_date, t.address, t.mobile, t.contact, t.contact_number, t.status, t.comment, t.photo FROM people_retire t LEFT JOIN dict_national s ON s.id = t.national_id LEFT JOIN job j ON j.id = t.retire_job_level_id
and t.code LIKE concat('%',#{condition.code},'%')
and t.name LIKE concat('%',#{condition.name}, '%')
and t.retire_job_name LIKE concat('%',#{condition.retireJobName},'%')
and t.retire_job_level_id = #{condition.retireJobLevelId}
and t.sex = #{condition.sex}
and t.national_id = #{condition.nationalId}
and t.education_name LIKE concat('%',#{condition.educationName},'%')
and t.birthday >= #{condition.birthdayMin}
and t.birthday <= #{condition.birthdayMax}
and t.political_name LIKE concat('%',#{condition.politicalName},'%')
and t.work_date >= #{condition.workDateMin}
and t.work_date <= #{condition.workDateMax}
and t.retire_date >= #{condition.retireDateMin}
and t.retire_date <= #{condition.retireDateMax}
and t.address LIKE concat('%',#{condition.address},'%')
and t.mobile LIKE concat('%',#{condition.mobile},'%')
and t.contact LIKE concat('%',#{condition.contact},'%')
and t.contact_number LIKE concat('%',#{condition.contactNumber},'%')
and t.status = #{condition.status}
and t.comment LIKE concat('%',#{condition.comment},'%')
LIMIT #{from}, #{size}

select count(*) count
from people_retire t


