mapper.select:
SELECT
	t.id,
	t.people_code,
	t.check_date,
	t.status,
	t.vacation_period
FROM people_timesheet t


		and t.people_code LIKE concat('%',#{condition.peopleCode},'%')


		and t.status = #{condition.status}


		 and t.check_date >= #{condition.checkDateMin} 


		 and t.check_date <= #{condition.checkDateMax} 


		 and t.vacation_period >= #{condition.vacatioinPeriodMin} 


		 and t.vacation_period <= #{condition.vacationPeriodMax} 


LIMIT #{from}, #{size}

SELECT count(t.id) count
FROM people_timesheet t


		and t.people_code LIKE concat('%',#{condition.peopleCode},'%')


		and t.status = #{condition.status}


		 and t.check_date >= #{condition.checkDateMin} 


		 and t.check_date <= #{condition.checkDateMax} 


		 and t.vacation_period >= #{condition.vacatioinPeriodMin} 


		 and t.vacation_period <= #{condition.vacationPeriodMax}
