mapper.select:
SELECT distinct people_code FROM people_transfer t LEFT JOIN people p ON t.people_code = p.code
and p.name LIKE concat('%', #{condition.peopleName},'%')
and t.from_school LIKE concat('%',#{condition.fromSchool},'%')
and t.to_school LIKE concat('%',#{condition.toSchool},'%')
and t.transfer_date >= #{condition.transferDateMin}
and t.transfer_date <= #{condition.transferDateMax}
and t.party_transfer_date >= #{condition.party_transferDateMin}
and t.party_transfer_date <= #{condition.partyTransferDateMax}

SELECT id, people_code, people_name, from_school, to_school, transfer_date FROM (SELECT * from people_transfer order by transfer_date desc) as b
people_code in
#{item}
group by people_code LIMIT #{from}, #{size}

SELECT t.id, t.people_code, t.people_name, t.people_type, t.from_school, t.to_school, t.transfer_date, t.ref_letter_no, t.salary_end_date, t.party_transfer_date FROM people_transfer t
and t.people_code LIKE concat('%', #{condition.peopleCode},'%')
LIMIT #{from},#{size}
