mapper.select:
SELECT t.id, t.code, t.name, t.department_id, t.life_insurance_base, t.job_insurance_base, t.wound_insurance_base, t.birth_insurance_base, t.health_insurance_base, t.annuity_base, t.status FROM people_total t
and t.status = #{condition.status}
and t.name LIKE concat('%',#{condition.name},'%')
LIMIT #{from}, #{size}

SELECT count(t.id) count FROM people_total t
and t.status = #{condition.status}
and t.name LIKE concat('%',#{condition.name},'%')

SELECT * FROM social_security t
t.people_code = #{condition.peopleCode}
order by pay_date desc LIMIT #{from}, #{size}

mapper.update:
update social_security
life_insurance_base = #{lifeInsuranceBase,jdbcType=DECIMAL}, life_insurance_school = #{lifeInsuranceSchool, jdbcType=DECIMAL}, life_insurance_people = #{lifeInsurancePeople, jdbcType=DECIMAL}, job_insurance_base = #{jobInsuranceBase, jdbcType=DECIMAL}, job_insurance_school = #{jobInsuranceSchool, jdbcType=DECIMAL}, job_insurance_people = #{jobInsurancePeople, jdbcType=DECIMAL}, wound_insurance_base = #{woundInsuranceBase, jdbcType=DECIMAL}, wound_insurance_school = #{woundInsuranceSchool, jdbcType=DECIMAL}, birth_insurance_base = #{birthInsuranceBase, jdbcType=DECIMAL}, birth_insurance_school = #{birthInsuranceSchool, jdbcType=DECIMAL}, health_insurance_base = #{healthInsuranceBase, jdbcType=DECIMAL}, health_insurance_school = #{healthInsuranceSchool, jdbcType=DECIMAL}, health_insurance_people = #{healthInsurancePeople, jdbcType=DECIMAL}, annuity_base = #{annuityBase, jdbcType=DECIMAL}, annuity_school = #{annuitySchool, jdbcType=DECIMAL}, annuity_people = #{annuityPeople, jdbcType=DECIMAL}, pay_date = #{payDate, jdbcType=VARCHAR}
where id = #{id, jdbcType=INTEGER}
